import React, { useState } from "react";

const proimg1 = "path-to-image.jpg"; // Replace this with actual image path

const initialList = [
  {
    id: 1,
    proimg: proimg1,
    productBadge: "Sale",
    name: "Product name goes here 1",
    price: "79.00",
    Cutprice: "300.00",
  },
  // Add more items if needed
];

function ProductSlider() {
  const [proSliderList, setProSliderList] = useState(initialList);

  const removeProduct = (id) => {
    const updatedList = proSliderList.filter((item) => item.id !== id);
    setProSliderList(updatedList);
  };

  return (
    <div className="product_slider">
      <div className="container">
        <div className="row">
          <div className="col-md-4">
            <div className="section_title">
              <h3>This Week’s Top Selling</h3>
            </div>
          </div>
        </div>
      </div>

      <div className="pro_slider">
        {proSliderList.map((proList) => (
          <div className="pro_items" key={proList.id}>
            <div className="pro_card">
              <div className="promo">{proList.productBadge}</div>
              <div className="pro_img">
                <img src={proList.proimg} alt="" className="img__contain" />
              </div>
              <div className="pro_cont">
                <div className="pro_rating_sec">
                  <ul className="pro_rating">
                    <li><i className="bx bxs-star"></i></li>
                    <li><i className="bx bxs-star"></i></li>
                    <li><i className="bx bxs-star"></i></li>
                    <li><i className="bx bxs-star"></i></li>
                    <li><i className="bx bxs-star"></i></li>
                  </ul>
                  <p className="pro_review">(5 Reviews)</p>
                </div>
                <h3 className="pro_title">{proList.name}</h3>
                <p className="pro_price">
                  {proList.price}
                  <span>{proList.Cutprice}</span>
                </p>
                <button onClick={() => removeProduct(proList.id)} className="btn btn-danger mt-2">
                  Remove
                </button>
              </div>
            </div>
          </div>
        ))}

        {proSliderList.length === 0 && (
          <p className="text-center mt-4">No products left.</p>
        )}
      </div>
    </div>
  );
}

export default ProductSlider;
