import React from "react";
import proimg1 from "../assets/images/pro_img1.png";
import ReactStars from "react-rating-stars-component";
import Slider from "react-slick";

const proSliderList = [
  {
    id: 1,
    proimg: proimg1,
    productBadge: "Sale",
    name: "Product name goes here 1",
    price: "79.00",
    Cutprice: "300.00",
  },
  {
    id: 2,
    proimg: proimg1,
    productBadge: "Sale",
    name: "Product name goes here 2",
    price: "79.00",
    Cutprice: "300.00",
  },
  {
    id: 3,
    proimg: proimg1,
    productBadge: "Sale",
    name: "Product name goes here 3",
    price: "79.00",
    Cutprice: "300.00",
  },
  {
    id: 4,
    proimg: proimg1,
    productBadge: "Sale",
    name: "Product name goes here 4",
    price: "79.00",
    Cutprice: "300.00",
  },
];

function ProductSlider() {
  const ratingChanged = (newRating) => {
    console.log("New Rating: ", newRating);
  };

  const settings = {
    slidesToShow: 4,
    slidesToScroll: 1,
    dots: false,
    arrows: true,
    centerMode: true,
  };

  return (
    <div className="product_slider">
      <div className="container">
        <div className="row">
          <div className="col-md-4">
            <div className="section_title">
              <h3>This Weekâ€™s Top Selling</h3>
            </div>
          </div>
        </div>
      </div>

      <div className="pro_slider">
        <Slider {...settings}>
          {proSliderList.map((proList) => (
            <div className="pro_items" key={proList.id}>
              <div className="pro_card">
                <div className="promo">{proList.productBadge}</div>
                <div className="pro_img">
                  <img src={proList.proimg} alt="" className="img__contain" />
                </div>
                <div className="pro_cont">
                  <div className="pro_rating_sec">
                    <div className="pro_rating">
                      <ReactStars
                        count={5}
                        onChange={ratingChanged}
                        size={20}
                        activeColor="#ffd700"
                      />
                    </div>
                    <p className="pro_review">(5 Reviews)</p>
                  </div>
                  <h3 className="pro_title">{proList.name}</h3>
                  <p className="pro_price">
                    ${proList.price} <span>${proList.Cutprice}</span>
                  </p>
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
}

export default ProductSlider;
