import React from "react";
import { useState } from "react";



function ToggleDiv() {
  const [showDiv, setShowDiv] = useState(false);
  const [showDiv2, setShowDiv2] = useState(true);

  const handleClick = () => {
    setShowDiv(!showDiv); // toggle true/false
    setShowDiv2(!showDiv2); // toggle true/false
  };


  return (
    <div>
      <button >Show/Hide Div</button>

      {showDiv &&  (
        <div style={{ marginTop: "10px", padding: "10px", background: "#eee" }}>
          This div is shown on click!
        </div>
      )}

      {showDiv2 && (
        <div style={{ marginTop: "10px", padding: "10px", background: "#eee" }}>
          This div is Hide on click!
        </div>
      )}
   


      
    </div>
  );
}

export default ToggleDiv;
